INSERT INTO public.categories (name, description) VALUES
('VÊTEMENTS', 'Matières de qualité (coton, soie, lin) adaptées à la peau. Design tendance pour toutes occasions. Tailles variées et ajustement confortable. Lavage facile et durable dans le temps.'),
('INJECTIONS', 'Sérums enrichis en vitamines et acides aminés. Solutions stériles pour soins esthétiques (peau, cheveux). Favorisent hydratation, élasticité et éclat. Destinées à des professionnels agréés uniquement.'),
('COMPLÉMENTS ALIMENTAIRES', 'Riche en vitamines essentielles (A, C, E) et minéraux. Formules pour la santé de la peau, des ongles et cheveux. Soutien du métabolisme et énergie naturelle. Composants 100% naturels et sûrs.'),
('LAITS, SAVONS, HUILES', 'Laits corporels nourrissants à base de karité, coco, amande. Savons enrichis en collagène et vitamine E pour une peau douce. Huiles essentielles pour hydratation intense et massage. Sans produits agressifs, adaptés à tous types de peau.'),
('HUMAINS HAIR', 'Cheveux 100% naturels, non traités chimiquement. Différentes textures : lisse, ondulé, bouclé. Extensions durables et faciles à entretenir. Convient aux techniques de pose variées (tissage, perruques).'),
('GAMMES BLANCHISSANTES', 'Crèmes à base d''acide kojique, glutathione. Sérums éclaircissants pour uniformiser le teint. Contiennent des agents hydratants et protecteurs. Testés dermatologiquement pour usage sûr.'),
('APHRODISIAQUE NATUREL', 'Composés de plantes et racines traditionnelles. Favorisent vitalité, endurance et libido. Produits naturels sans additifs chimiques. Formules pour homme et femme.'),
('MAQUILLAGE & BEAUTÉ', 'Fond de teint, poudres et rouges à lèvres longue tenue. Pigments riches, textures légères et faciles à appliquer. Hypoallergéniques et adaptés à tous types de peaux.'),
('ACCESSOIRES BEAUTÉ', 'Brosses, peignes, éponges et outils professionnels. Matériaux durables pour un usage prolongé. Design ergonomique pour un confort optimal.'),
('SOINS CAPILLAIRES', 'Shampoings nourrissants aux huiles naturelles. Masques réparateurs pour cheveux secs ou abîmés. Sérums anti-chute enrichis en kératine et biotine.'),
('PARFUMS & DÉODORANTS', 'Fragrances de luxe aux notes florales, boisées, fruitées. Déodorants doux pour une fraîcheur longue durée. Sans alcool agressif pour la peau.');
